    public void setup(int age, gender sex, GameObject constituency, chamber leg, List<characteristic> charact = null) //possible ages and genders are dependant on legislative body rules (e.g. there may be a minimum age) and thus they are passed to this class
    {
        legislature = leg;
        Age = age;
        Gender = sex;
        selectName();
        Constituency = constituency.name;
        Constituency_GO = constituency;
        Party = transform.parent.gameObject;
        align();
        if(charact == null && Random.Range(0,5)==0) //if none have been provided, chance to randomly assign one
        {
            characteristics = new List<characteristic>() { (characteristic)Random.Range(0, (int)System.Enum.GetValues(typeof(characteristic)).Cast<characteristic>().Max()) };
        }
        else
        {
            characteristics = charact;
        }
    }

    public GameObject policyPosition() //returns what party the politician aligns with
    {
        representativeSystem rS= Party.transform.parent.parent.gameObject.GetComponent<CountryController>().LegislativeChambers[0].GetComponent<LegislatureController>().Representative;

        if (rS == representativeSystem.Trustee)
        {
            float r = Random.Range(0.0f, 1.0f);
            if (r <= alignmentPrimary) return Party;
            else if (r <= (alignmentPrimary + alignmentSecondary)) return PartySecondary;
            else return PartyTertiary;
        }
        else
        {
            float r = Random.Range(0.0f, 1.0f);
            int c = Constituency_GO.GetComponent<ConstituencyController>().candidatesPopularity.Count;
            float t = 0.0f;
            for(int x=0; x<c;x++)
            {
                t += Constituency_GO.GetComponent<ConstituencyController>().candidatesPopularity[x];
                if (r <= t) return Constituency_GO.GetComponent<ConstituencyController>().candidates[x].GetComponent<PoliticianController>().Party;
            }
        }

        return null;
    }

    void align()
    {
        GameObject partiesParent = transform.parent.parent.gameObject;
        bool selectingSecondary = true;
        bool selectingTertiary = true;
        string[] values = System.Enum.GetNames(typeof(platform));
        int count = values.Length;

        while (selectingSecondary)
        {
            GameObject candidate = partiesParent.transform.GetChild(Random.Range(0, count)).gameObject;
            if(candidate!=Party)
            {
                PartySecondary = candidate;
                selectingSecondary = false;
            }
        }

        while (selectingTertiary)
        {
            GameObject candidate = partiesParent.transform.GetChild(Random.Range(0, count)).gameObject;
            if (candidate != Party && candidate != PartySecondary)
            {
                PartyTertiary = candidate;
                selectingTertiary = false;
            }
        }

        alignmentPrimary = Random.Range(0.5f, 1.0f);
        alignmentSecondary = Random.Range(0.0f, (1.0f - alignmentPrimary) );
        alignmentTertiary = Random.Range(0.0f, (1.0f - alignmentPrimary - alignmentSecondary) );
    }

    void selectName()
    {
        if(Gender==gender.Female)
        {
            Name = namesFemale[Random.Range(0, namesFemale.Count - 1)] + " " + surnames[Random.Range(0, surnames.Count - 1)];
        }
        else
        {
            Name = namesMale[Random.Range(0, namesMale.Count - 1)] + " " + surnames[Random.Range(0, surnames.Count - 1)];
        }
        gameObject.name = Name;
    }

    public void adjustPopularity(float amount)
    {
        ConstituencyController c = Constituency_GO.GetComponent<ConstituencyController>();
        int pos = c.candidates.IndexOf(gameObject);
        float adj = c.candidatesPopularity[pos] * amount;
        c.candidatesPopularity[pos] += adj;
        for (int x = 0; x < c.candidates.Count; x++)
        {
            if (x == pos) continue;
            else
            {
                c.candidatesPopularity[x] -= adj / (c.candidates.Count-1);
            }
        }
    }

    public void increaseAge()
    {
        Age += 1;
        Alive = Random.Range(0.0f, 1.0f) <= deathChance;
    }
    public bool isDead() { return !Alive; }
    public bool isAlive() { return Alive; }
